import React from 'react';
import { Image, Pressable, StyleSheet, Text, View } from 'react-native';
import {
  heightPercentageToDP as hp,
  widthPercentageToDP as wp,
} from 'react-native-responsive-screen';
import QRCode from 'react-native-qrcode-svg';
import { useSelector } from 'react-redux';

const ScanImage = '../../assets/images/Scan.png';
const BackIcon = '../../assets/images/Iconly_Curved_Arrow.png';
const  qrCodeWidth = hp('34');
const styles = StyleSheet.create({
  fornaxBox: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'flex-start',
  },
  textStyle: {
    fontSize: 20,
    color: '#ffffff',
    lineHeight: 23,
    fontFamily: 'Quicksand-Bold',
    textAlign: 'center',
  },
  backIcon: {
    marginLeft: 26,
    marginTop: 32,
  },
  fornaxIcon: {
    // width:80,
    // height:80,
    // width:  hp(9),
    // height: hp(9),
    marginBottom: 30,
  },
  fornaxInnerBox: {
    flex: 0,
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'flex-start',
    marginTop: hp('5'),
    marginBottom: hp('4'),
  },
  fornaxMiniText: {
    fontSize: 16,
    color: '#bdbdbd',
    textAlign: 'center',
    fontFamily: 'Quicksand-Medium',
  },
  inputBox: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    height: 60,
    borderRadius: 15,
    fontFamily: 'Quicksand-Medium',
    backgroundColor: '#ffffff',
    color: '#bdbdbd',
    fontSize: 16,
    width: 340,
    marginBottom: 20,
  },
  input: {
    fontFamily: 'Quicksand-Medium',
    fontSize: 16,
    width: 340,
    paddingHorizontal: 20,
  },
  qrCodeImg: {
    alignItems: 'center',
    alignSelf: 'center',
    marginBottom: 20,
    height: qrCodeWidth,
    width: qrCodeWidth,
    borderWidth: 5,
    borderColor: 'aliceblue',
    borderRadius: 30,
    paddingTop: 28,
    backgroundColor: 'aliceblue',
  },
  topLine: {
    height: 6,
    backgroundColor: '#021124',
    width: wp('49'),
    position: 'absolute',
    top: -6,
  },
  leftLine: {
    width: 6,
    backgroundColor: '#021124',
    height: hp('23'),
    position: 'absolute',
    left: -6,
    top: 40,
  },
  rightLine: {
    width: 6,
    backgroundColor: '#021124',
    height: hp('23'),
    position: 'absolute',
    right: -6,
    top: 40,
  },
  bottomLine: {
    height: 6,
    backgroundColor: '#021124',
    width: wp('49'),
    position: 'absolute',
    bottom: -6,
  },
});

const Scan = (props: any) => {
  const navigate = props.navigation.navigate;

  const { accounts, defaultAddress } = useSelector(({ wallet }: any) => {
    return {
      accounts: wallet?.accounts,
      defaultAddress: wallet?.defaultAddress,
    };
  });

  return (
    <>
      <View>
        <Pressable onPress={() => navigate('Dashboard')}>
          <Image style={styles.backIcon} source={require(BackIcon)} />
        </Pressable>
      </View>
      <View style={styles.fornaxBox}>
        <View style={styles.fornaxInnerBox}>
          <Image style={styles.fornaxIcon} source={require(ScanImage)} />
          <Text style={styles.textStyle}>Scan Me</Text>
          <Text style={styles.fornaxMiniText}>My QR code</Text>
        </View>
        <View style={styles.qrCodeImg}>
          {/*<View style={styles.topLine} />*/}
          {/*<View style={styles.leftLine} />*/}
          <QRCode
            value={
              defaultAddress || (accounts.length > 0 && accounts[0]?.address)
            }
            size={(qrCodeWidth-60)}
            color="#363853"
            backgroundColor="aliceblue"
          />
          {/*<View style={styles.rightLine} />*/}
          {/*<View style={styles.bottomLine} />*/}
        </View>
        <View>
          <Text style={styles.fornaxMiniText}>Scan this to pay</Text>
          <Text style={styles.fornaxMiniText}>debt to you more easily</Text>
          <Text
            style={[
              styles.fornaxMiniText,
              { marginTop: 25, color: '#2d9cdb' },
            ]}>
            Or scan to pay your bill
          </Text>
        </View>
      </View>
    </>
  );
};

export default Scan;
